import React, { Component } from 'react';

import "./style.css";

import Header from '../../components/Header';
import Hamburguer from '../../assets/images/hamburguer';
import Drawer from '../../components/SideMenu';
import Atividade from './components/Atividade';
import Conteudo from './components/Conteudo';
import SearchInput from './components/SearchInput';
import { withRouter } from 'react-router-dom';

class Atividades extends Component {

    openTask = () => {
        const { history } = this.props;
        history.push("/atividade");
    }

    render = () => {

        return(
            <>
            <Header drawer={true} icon={<Hamburguer />} title={"Turma"}></Header>
            <Drawer ></Drawer>

            <div className={"title-container"}>
                <p className={"title"}> Nome da Turma</p>
            </div>
            <div className={"title-container"}>
                <p className={"title"}> Atividades Agendadas</p>
            </div>
            <Atividade onClick={this.openTask}></Atividade>
            <Atividade onClick={this.openTask}></Atividade>
            <Atividade onClick={this.openTask}></Atividade>
            <Atividade onClick={this.openTask}></Atividade>
            <Atividade onClick={this.openTask}></Atividade>
            <div className={"title-container"}>
                <p className={"title"}>Conteúdos</p>
            </div>
            <div className={"row"}>
                <SearchInput></SearchInput>
            </div>
            <div className={"row"}>
                <div className={"col"}>
                    <Conteudo></Conteudo>
                </div>
                <div className={"col"}>
                    <Conteudo></Conteudo>
                </div>
                <div className={"col"}>
                    <Conteudo></Conteudo>
                </div>
            </div>
            <div className={"row"}>
                <div className={"col"}>
                    <Conteudo></Conteudo>
                </div>
                <div className={"col"}>
                    <Conteudo></Conteudo>
                </div>
                <div className={"col"}>
                    <Conteudo></Conteudo>
                </div>
            </div>
            </>
        )
    }

}

export default withRouter(Atividades);
