import React, { Component } from 'react';



class Avatar extends Component {

    getAvatar = (name) => {
        if (name.split(" ")[0] == "Alexandre") {
            return require("./alexandre.svg");
        }
    }

    render = () => {
        const { name } = this.props;

        return (
            <div style={{
                display: 'flex',
                flexDirection: 'row',
                height:32,
                width:220,
                borderRadius: 50,
                justifyContent:'center',
                alignItems:'center',
            }}>

                    <div style={{ zIndex:1,
                                  backgroundColor: "gray",
                                  borderRadius: 100,
                                  padding: 4,
                                  height:'inherit',
                                  display:'flex',
                                  alignItems:'center',
                                  justifyContent:'center'
                                   }}>
                        <img src={this.getAvatar(name)} />
                    </div>
                <div style={{
                    paddingTop: 6,
                    paddingBottom: 6,
                    backgroundColor:'green',
                    display: 'flex',
                    flexDirection: 'row',
                    alignItems: 'center',
                    justifyContent: 'flex-start',
                    borderRadius: 50,
                    position:'relative',
                    right:'24px',
                    paddingLeft:32,

                    height:'inherit',
                    backgroundColor:'#EDEAEA'


                }}>
                <p style={{ margin: 0 }}>Alexandre Silva</p>
                </div>
            </div>

        )
    }
}

export default Avatar;
