import React, { Component } from 'react';



class Avatar extends Component {

    getAvatar = (name) => {
        if (name.split(" ")[0] == "Alexandre") {
            return require("./alexandre.svg");
        }
    }

    render = () => {
        const { name } = this.props;

        return (
            <div style={{
                display: 'flex',
                flexDirection: 'row',
                backgroundColor: '#EDEAEA',




                paddingRight: 24,
            }}>

                    <div style={{ backgroundColor: "gray", borderRadius: 100, padding: 12, }}>
                        <img src={this.getAvatar(name)} />
                    </div>
                <div style={{
                    paddingTop: 6,
                    paddingBottom: 6,
                    backgroundColor:'green',
                    display: 'flex',
                    flexDirection: 'row',
                    alignItems: 'center',
                    justifyContent: 'flex-start',
                    borderRadius: 50,
                    position:'absolute',
                    zIndex:-1,

                }}>
                <p style={{ margin: 0 }}>Alexandre Silva</p>
                </div>
            </div>

        )
    }
}

export default Avatar;
